CREATE TABLE IF NOT EXISTS `blog_entriesDataImages` (`entryId` int(10) unsigned NOT NULL, `uri` varchar(191) COLLATE utf8mb4_unicode_ci NOT NULL, PRIMARY KEY (`entryId`,`uri`), KEY `entryId` (`entryId`), CONSTRAINT `blog_entriesDataImages_ibfk_1` FOREIGN KEY (`entryId`) REFERENCES `blog_entries` (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;